from django.apps import appsfrom django import templatefrom django.urls import reverse, NoReverseMatchregister = template.Library()@register.simple_tagdef all_apps_urls():    urls = list()    app_configs = list()    apps_labels = ['services', 'employees', 'productions']    for app_label in apps_labels:        app_configs.append(apps.get_app_config(app_label))        for app_config in app_configs:            for model in app_config.get_models():                model_name = model.__name__.lower()                url_name = f"{app_label}:{model_name}_list"                try:                    url = reverse(url_name)                    urls.append(url)                except NoReverseMatch:                    pass    return urls