from employees.models import Employee, Position, Departmentfrom django.db import modelsfrom django.utils.translation import gettext_lazyclass LeaveForm(models.Model):    name = models.CharField(_('name'), max_length=100, )    position = models.ForeignKey(Position, on_delete=models.SET_NULL, null=True, blank=False)    department = models.ForeignKey(Department, on_delete=models.CASCADE)    reason = models.CharField(_('reason for leave'), max_length=300, blank=True)    from_date = models.DateTimeField(auto_created=True)    to_date = models.DateTimeField(auto_created=True)    total = models.IntegerField(default=1, blank=True)    employee = models.ForeignKey(Employee, related_name="employee", on_delete=models.CASCADE)    dept_head = models.ForeignKey(Employee,related_name="head_office", on_delete=models.CASCADE)    is_approved = models.BooleanField(default=False)    request_date = models.DateTimeField(auto_now_add=True)    def __str__(self):        return self.employee, + str(self.from_date) + str(self.total) + "long"