from ..models import Product, Promotionfrom django.urls import reverse_lazy, reverse, NoReverseMatchfrom django import templatefrom django.template.loader import get_templateregister = template.Library()@register.simple_tagdef menu():    app_label = 'productions'    urls = []    urls_name = [        'create_product', 'create_promotion',    ]    for url_name in urls_name:        try:            url = reverse(f'{app_label}:{url_name}')            urls.append(url)            print(url_name)        except NoReverseMatch:            print("The url is not work.")    return urls